import { useState } from 'react'
import styles from './Papers.module.css'
import { UnrelatedPannel } from './Unrelated.js'
import { ReviewedPannel } from './Reviewed.js'
import { ToReviewPannel } from './ToReview.js'

function PapersHeader() {
    return <h1 className={styles.papersHeader}>Papers</h1>;
}

function PapersMainSeparator() {
    return <div className={styles.mainSeparator}></div>;
}

function PapersTableRow(props) {
    return (
        <tr>
            <td className={styles.paperId}>{props.paperId}</td>
            <td className={styles.paperTitle}>{props.paperTitle}</td>
        </tr>
    );
}

function PapersTable() {
    const papers = [
        {id: 1, title: "Title of the first paper"},
        {id: 2, title: "Title of the second paper"},
        {id: 3, title: "Title of paper no 3"},
        {id: 4, title: "Title of the fourth paper"},
        {id: 5, title: "Title of the fifth paper"},
        {id: 6, title: "Title of the 6th paper"},
        {id: 7, title: "Title of the seventh"},
        {id: 8, title: "Title of the 8th paper"}
    ];

    return (
        <table className={styles.papersTable}>
            <tr>
                <th className={styles.papersTableHeader}>Paper ID</th>
                <th className={styles.papersTableHeader}>Title</th>
            </tr>
            {papers.map(paper => <PapersTableRow key={paper.id} paperId={paper.id} paperTitle={paper.title} />)}
        </table>
    );
}

function Placeholder() {
    return (
        <div className={styles.placeholder}>
        </div>
    );
}

function FunctionalityContainer(props) {
    return (
        <div className={styles.functionalityContainer}>
            {props.option == "all" && <Placeholder />}
            {props.option == "unrelated" && <UnrelatedPannel />}
            {props.option == "reviewed" && <ReviewedPannel />}
            {props.option == "toReview" && <ToReviewPannel />}
        </div>
    );
}

function SecondarySeparator() {
    return <div className={styles.secondarySeparator}></div>
}

function AllPapersButton(props) {
    const allButtonValue = "all";

    const handleClick = () => {
        props.setOption(allButtonValue);
    };

    return <button className={styles.papersButton} onClick={handleClick} type="button">All Papers</button>
}

function DetailsButton(props) {
    const detailsButtonValue = "selectedPage";

    const handleClick = () => {
        props.setContent(detailsButtonValue);
    };

    return <button className={styles.papersButton} onClick={handleClick} type="button">Details</button>
}

function ToReviewButton(props) {
    const toReviewButtonValue = "toReview";

    const handleClick = () => {
        props.setOption(toReviewButtonValue);
    }

    return <button className={styles.papersButton} onClick={handleClick} type="button">To review</button>
}

function ReviewedButton(props) {
    const reviewedButtonValue = "reviewed";

    const handleClick = () => {
        props.setOption(reviewedButtonValue);
    };

    return <button className={styles.papersButton} onClick={handleClick} type="button">Reviewed</button>
}

function UnrelatedButton(props) {
    const unrelatedButtonValue = "unrelated";

    const handleClick = () => {
        props.setOption(unrelatedButtonValue);
    };

    return <button className={styles.papersButton} onClick={handleClick} type="button">Unrelated</button>
}

function ViewPapers(props) {
    return (
        <div className={styles.viewPapers}>
            <AllPapersButton setOption={props.setOption} />
            <DetailsButton setContent={props.setContent} />
        </div>
    );
}

function FilterPapers(props) {
    return (
        <div className={styles.filterPapers}>
            <ToReviewButton setOption={props.setOption} />
            <ReviewedButton setOption={props.setOption} />
            <UnrelatedButton setOption={props.setOption} />
        </div>
    );
}

function ButtonGroup(props) {
    return (
        <div className={styles.buttonGroup}>
            <ViewPapers setOption={props.setOption} setContent={props.setContent} />
            <FilterPapers setOption={props.setOption} />
        </div>
    );
}


function ToggleArea(props) {
    let [option, setOption] = useState("unrelated");

    return (
        <div className={styles.toggleArea}>
            <FunctionalityContainer option={option} />
            <SecondarySeparator />
            <ButtonGroup setOption={setOption} setContent={props.setContent}/>
        </div>
    );
}

function PapersContent(props) {
    return (
        <div className={styles.contentArea}>
            <PapersTable />
            <ToggleArea setContent={props.setContent}/>
        </div>
    );
}

function PapersPage(props) {
    return (
        <div className={styles.papersArea}>
            <PapersHeader />
            <PapersMainSeparator />
            <PapersContent setContent={props.setContent}/>
        </div>
    );
}

export { PapersPage }
